import React, { useState, useEffect } from "react";


function Bot(contents){

    const{
        name,
        type,
        id
    } = contents;

    const [task1, setTask1] = useState("");
    const [task2, setTask2] = useState("");
    const [task3, setTask3] = useState("");
    const [task4, setTask4] = useState("");
    const [task5, setTask5] = useState("");

    const [complete1, setComplete1] = useState(true);
    const [complete2, setComplete2] = useState(true);
    const [complete3, setComplete3] = useState(true);
    const [complete4, setComplete4] = useState(true);
    const [complete5, setComplete5] = useState(true);
    const [completeAll, setCompleteAll] = useState(true);

    const [loading1, setLoading1]= useState(false); 
    const [loading2, setLoading2]= useState(false); 
    const [loading3, setLoading3]= useState(false); 
    const [loading4, setLoading4]= useState(false); 
    const [loading5, setLoading5]= useState(false); 

    useEffect(() => {
        if (type === "Unipedal" || type === "Aeronautical") {
            setTask1("do the dishes (1s)");
            setTask2("sweep the house (3s)");
            setTask3("do the laundry (10s)");
            setTask4("take out the recycling (4s)");
            setTask5("make a sammich (7s) ");
        } else if (type === "Bipedal" || type === "Radial") {
            setTask1("mow the lawn ()");
            setTask2("rake the leaves ()");
            setTask3("give the dog a bath ()");
            setTask4("bake some cookies ()");
            setTask5("wash the car (20s)");
        } else if (type === "Quadrupedal" || type === "Arachnid") {
            setTask1("do the dishes ()");
            setTask2("do the laundry ()");
            setTask3("make a sammich ()");
            setTask4("rake the leaves ()");
            setTask5("bake some cookies (8s)");
        }
    }, []); 
    //Do tasks in a certain amount of time
    async function doTask (task, id) {

        setLoading(id);
        if (task === "do the dishes (1s)"){
            return new Promise(function(resolve) {
                setTimeout(function() {
                    console.log(task);
                    setComplete(id);
                    resolve();
                }, 1000);
            });
        }else if (task === "sweep the house") {
            return new Promise(function(resolve) {
                setTimeout(function() {
                    console.log(task);
                    setComplete(id);
                    resolve();
                }, 3000);
            });
        } else if (task === "do the laundry") {
            return new Promise(function(resolve) {
                setTimeout(function() {
                    console.log(task);
                    setComplete(id);
                    resolve();
                }, 10000);
            });
        } else if (task === "take out the recycling") {
            return new Promise(function(resolve) {
                setTimeout(function() {
                    console.log(task);
                    setComplete(id);
                    resolve();
                }, 4000);
            });
        } else if (task === "make a sammich") {
            return new Promise(function(resolve) {
                setTimeout(function() {
                    console.log(task);
                    setComplete(id);
                    resolve();
                }, 7000);
            });
        } else if (task === "mow the lawn") {
            return new Promise(function(resolve) {
                setTimeout(function() {
                    console.log(task);
                    setComplete(id);
                    resolve();
                }, 20000);
            });
        } else if (task === "rake the leaves") {
            return new Promise(function(resolve) {
                setTimeout(function() {
                    console.log(task);
                    setComplete(id);
                    resolve();
                }, 18000);
            });
        } else if (task === "give the dog a bath") {
            return new Promise(function(resolve) {
                setTimeout(function() {
                    console.log(task);
                    setComplete(id);
                    resolve();
                }, 14500);
            });
        } else if (task === "bake some cookies") {
            return new Promise(function(resolve) {
                setTimeout(function() {
                    console.log(task);
                    setComplete(id);
                    resolve();
                }, 8000);
            });
        } else if (task === "wash the car") {
            return new Promise(function(resolve) {
                setTimeout(function() {
                    console.log(task);
                    setComplete(id);
                    resolve();
                }, 20000);
            });
        }
    }
    //
    const loading = (id) => {
        if (id === 1 && loading1) {
            return "is-loading";
        } else if (id === 2 && loading2) {
            return "is-loading";
        } else if (id === 3 && loading3) {
            return "is-loading";
        } else if (id === 4 && loading4) {
            return "is-loading";
        } else if (id === 5 && loading5) {
            return "is-loading";
        }
    }
    const setLoading = (id) => {
        if(id === 1){
            setLoading1(true);
        }else if(id === 2){
            setLoading2(true);
        }else if(id === 3){
            setLoading3(true);
        }else if(id === 4){
            setLoading4(true);
        }else if(id === 5){
            setLoading5(true);
        }
    }
    //
    const setComplete = (id) => {
        if(id === 1){
            setComplete1(false);
        }else if(id === 2){
            setComplete2(false);
        }else if(id === 3){
            setComplete3(false);
        }else if(id === 4){
            setComplete4(false);
        }else if(id === 5){
            setComplete5(false);
        }
    }
    //
    async function completeAllTask() {
        function taskOne(){
            return doTask(task1, 1);
        }
        function taskTwo(){
            return doTask(task2, 2);
        }
        function taskThree(){
            return doTask(task3, 3);
        }
        function taskFour(){
            return doTask(task4, 4);
        }
        function taskFive(){
            return doTask(task5, 5);
        }

        taskOne().then(taskTwo).then(taskThree).then(taskFour).then(taskFive);
        setCompleteAll(false);
    }

    return (
        <div className="Bot">
            <h1>{name}</h1>
            <h1>{type}</h1>

            {completeAll &&
                <button class="completeAllButton" onClick={() => {completeAllTask()}}>finish every task</button>
            }
            <br></br>

            <div>
                <label>{task1 + ""}</label>
                {complete1
                    ?  
                    <button class={"button is-black " + loading(1)} onClick={()=>{doTask(task1, 1)}}>do task</button>
                    : <p>complete!</p>
                }
            </div>

            <div>
                <label>{task2 + "task 1"}</label>
                {complete2
                    ?  <button class={"button is-black " + loading(2)} onClick={()=>{doTask(task2, 2)}}>do task</button>
                    : <p>complete!</p>
                }
            </div>
            <div>
                <label>{task3 + "task 1"}</label>
                {complete3
                    ?  <button class={"button is-black " + loading(3)} onClick={()=>{doTask(task3, 3)}}>do task</button>
                    : <p>complete!</p>
                }
            </div>
            <div>
                <label>{task4 + "task 1"}</label>
                {complete4
                    ?  <button class={"button is-black " + loading(4)} onClick={()=>{doTask(task4, 4)}}>do task</button>
                    : <p>complete!</p>
                }
            </div>
            <div>
                <label>{task5 + "task 1"}</label>
                {complete5
                    ?  <button class={"button is-black " + loading(5)} onClick={()=>{doTask(task5, 5)}}>do task</button>
                    : <p>complete!</p>
                }
            </div>




        </div>
    );


}
export default Bot;
