import React, { useState, useEffect } from "react";
import './App.css';

function App() {

  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [id, setId] = useState("");
  const [list, setList] = useState("");


  const createBot = () => {
    //Prevention of bot not having name or type
    if (name != "" || type != ""){
      //setList(list.concat( <Bot name={name} type={type} id={id} key={id}/>));

      setName("");
      setId(id+1);
    }
  } 

  return (
    <div className="App">
      <div >
      <input className="inputName" value={name} placeholder="Enter Bot Name" onChange={ (e) => {setName(e.target.value)}} />
      <br></br>

      <div onChange={(e) => {setType(e.target.value); console.log(e.target.value);}}> 
        <input className="botType" type="botType" value="Unipedal" name="gender"/> 
        <input className="botType" type="botType" value="Bipedal" name="gender"/> 
        <input className="botType" type="botType" value="Quadrupedal" name="gender"/> 
        <br></br>
        <input className="botType" type="botType" value="Arachnid" name="gender"/> 
        <input className="botType" type="botType" value="Radial" name="gender"/> 
        <input className="botType" type="botType" value="Aeronautical" name="gender"/> 
      </div>

      <button className="botButton" onClick={() => {createBot(); console.log(type + " " + name)}} >Create Bot</button>

      <div className="makeBot">
          {list}
      </div>


      </div>
    </div>
  );
}

export default App;
